controller.menu.onEvent(ControllerButtonEvent.Pressed, function () {
    power.deepSleep()
})
controller.left.onEvent(ControllerButtonEvent.Pressed, function () {
    switch (gameMode) {
        case GameMode.Menu:
            decreaseLvlChoice()
            break

        case GameMode.Game:
            music.playTone(Note.A, 50)
            currentPiece.moveLeft()
            break
    }   // switch (gameMode)
})
controller.right.onEvent(ControllerButtonEvent.Pressed, function () {
    switch (gameMode) {
        case GameMode.Menu:
            increaseLvlChoice()
            break

        case GameMode.Game:
            music.playTone(Note.A, 50)
            currentPiece.moveRight()
            break
    }   // switch (gameMode)
})
controller.up.onEvent(ControllerButtonEvent.Pressed, function () {
    switch (gameMode) {
        case GameMode.Menu:
            updateCursorPosition()
            break

        case GameMode.Game:
            startPause()
            break

        case GameMode.Pause:
            gameMode = GameMode.Game
            drawGrid()
            break
    }   // switch (gameMode)
})
controller.down.onEvent(ControllerButtonEvent.Pressed, function () {
    switch (gameMode) {
        case GameMode.Menu:
            updateCursorPosition()
            break

        case GameMode.Game:
            music.playTone(Note.B, 50)
            currentPiece.moveDown()
            break
    }   // switch (gameMode)
})
controller.A.onEvent(ControllerButtonEvent.Pressed, function () {
    switch (gameMode) {
        case GameMode.Title:
            titlePicture = undefined
            startMenu()
            break

        case GameMode.Menu:
            option1.destroy()
            option2.destroy()
            cursor.destroy()
            lvlChoice.destroy()
            diffChoice.destroy()
            menuPicture = undefined

            startGame()
            break

        case GameMode.Game:
            currentPiece.rotCW()
            music.playTone(Note.A, 50)
            music.playTone(Note.B, 50)
            break

        case GameMode.Pause:
            break

        case GameMode.End:
            grid = undefined
            bgPicture = undefined
            nextPiece = undefined
            currentPiece = undefined
            game.reset()
            break
    }   // switch (gameMode)
})
controller.B.onEvent(ControllerButtonEvent.Pressed, function () {
    switch (gameMode) {
        case GameMode.Title:
            titlePicture = undefined
            startMenu()
            break

        case GameMode.Menu:
            option1.destroy()
            option2.destroy()
            cursor.destroy()
            lvlChoice.destroy()
            diffChoice.destroy()
            menuPicture = undefined

            startGame()
            
            break

        case GameMode.Game:
            currentPiece.rotCCW()
            music.playTone(Note.B, 50)
            music.playTone(Note.A, 50)
            break
    }   // switch (gameMode)
})
controller.left.onEvent(ControllerButtonEvent.Repeated, function () {
    if (gameMode == GameMode.Game) currentPiece.moveLeft()
})
controller.right.onEvent(ControllerButtonEvent.Repeated, function () {
    if (gameMode == GameMode.Game) currentPiece.moveRight()
})
controller.down.onEvent(ControllerButtonEvent.Repeated, function () {
    if (gameMode == GameMode.Game) {
        currentPiece.moveDown()
        if (currentPiece.y > 1) { dropPoints++ }
    }
})


