game.onUpdate(function () {
    switch (gameMode) {
        case GameMode.Title:
            break

        case GameMode.Menu:
            break

        case GameMode.Game:
            if ((game.runtime() - currentTime > speed)) {
                currentTime = game.runtime()
                if (currentPiece.collision(0, 1)) {
                    if (currentPiece.y > 0) {
                        music.playTone(Note.C, 100)
                        currentPiece.lock()
                        info.changeScoreBy(Math.floor(dropPoints / 10) * (level + 1))
                        checkLine()
                        levelUp()
                        newPiece()
                        dropPoints = 0
                    } else {
                        startEnd()
                    }
                } else {
                    currentPiece.moveDown()
                }
            }
            break

        case GameMode.Pause:
            break

        case GameMode.End:
            break
    }   // switch (gameMode)
})