const ROW = 19    // Grid ..
const COL = 9     // .. size
const SQ = 6       // square size
const VACANT = 1  // color for blank squares
const BOARD = 2

const PIECES = [I, J, L, O, S, T, Z]
const COLOR = [8, 14, 12, 7, 4, 10, 9]

const linePoints = 50
const levelPoints = 200

///// FUNCTIONS //////////////////////////////////////////////
// Display the minimal unit of a piece
function drawSquare(x: number, y: number, color: number) {
    let X = 20 + x * SQ
    let Y = y * SQ
    bgPicture.fillRect(X, Y, SQ, SQ, color)
    if (color != VACANT) {
        let Clight
        let Cdark
        switch (color) {
            case 8: {
                Clight = 7
                Cdark = 14
                break
            }
            case 14: {
                Clight = 4
                Cdark = 2
                break
            }
            case 12: {
                Clight = 9
                Cdark = 13
                break
            }
            case 7: {
                Clight = 6
                Cdark = 14
                break
            }
            case 4: {
                Clight = 3
                Cdark = 5
                break
            }
            case 10: {
                Clight = 15
                Cdark = 11
                break
            }
            case 9: {
                Clight = 15
                Cdark = 11
                break
            }
        }
        bgPicture.drawLine(X, Y, X + 5, Y, Clight) //up
        bgPicture.drawLine(X, Y + 5, X + 5, Y + 5, Cdark) //down
        bgPicture.drawLine(X, Y + 1, X, Y + 4, Clight) //left up
        bgPicture.drawLine(X + 5, Y + 1, X + 5, Y + 5, Cdark) //right up
    }
}
// The grid is composed of 10x20 squares
function drawGrid() {
    for (let row = 0; row <= ROW; row++) {
        for (let col = 0; col <= COL; col++) {
            drawSquare(col, row, grid[row][col])
        }
    }
}
// Check if lines are completed
function checkLine() {
    let line: boolean = true
    let nbLinesCompleted: number = 0
    let checkAgain: boolean = true
    while (checkAgain == true) {
        checkAgain = false
        for (let row = ROW; row >= 0; row--) {
            line = true
            for (let col = 0; col <= COL; col++) {
                if (grid[row][col] == VACANT) { line = false }
            }
            if (line == true) {
                nbLines++
                nbLinesCompleted++
                //music.playTone(Note.E, 50)
                bgPicture.fillRect(130, 35, 20, 8, BOARD)
                bgPicture.print(nbLines.toString(), 130, 35, 15)
                for (let r = row; r >= 1; r--) {
                    for (let c = 0; c <= COL; c++) {
                        grid[r][c] = grid[r - 1][c]
                    }
                }
                grid[0] = [VACANT, VACANT, VACANT, VACANT, VACANT, VACANT, VACANT, VACANT, VACANT, VACANT]
                checkAgain = true
            }
        }
    }
    info.changeScoreBy(linePoints * (nbLinesCompleted) * (level + difficulty + 1))
    drawGrid()
}
// Level up
function levelUp() {
    if (level - startLevel < Math.floor(nbLines / 10)) {
        level++
        /*music.playTone(Note.E, 50)
        music.playTone(Note.F, 50)
        music.playTone(Note.E, 50)*/
        bgPicture.fillRect(130, 20, 20, 8, BOARD)
        bgPicture.print(level.toString(), 130, 20, 15)
        speed = setSpeed(level)
        info.changeScoreBy(levelPoints * (level + difficulty + 1))
    }
}
function setSpeed(lvl: number) {
    let spd: number
    if (lvl <= 8) {
        spd = 1000 - (lvl * 100)
    } else {
        spd = 150
    }
    return spd
}
// Create a new piece
function newPiece() {
    nextPiece.undraw()
    currentPiece = nextPiece
    currentPiece.x = 3
    currentPiece.y = -2
    currentPiece.draw()

    nextPiece = new Piece()
    nextPiece.draw()
}


///// Piece object creation //////////////////////////////////////////////
class Piece {
    tetromino: number[][][]
    color: number
    rotation: number
    active: number[][]
    x: number
    y: number
    constructor() {
        let rand: number = Math.randomRange(0, 6)
        this.tetromino = PIECES[rand]
        this.color = COLOR[rand]
        this.rotation = 0
        this.active = this.tetromino[this.rotation]
        this.x = 14
        this.y = 10
    }

    draw() {
        for (let row = 0; row <= this.active.length - 1; row++) {
            for (let col = 0; col <= this.active[row].length - 1; col++) {
                if (this.active[row][col] == 1) {
                    drawSquare(this.x + col, this.y + row, this.color)
                }
            }
        }
    }

    undraw() {
        for (let row = 0; row <= this.active.length - 1; row++) {
            for (let col = 0; col <= this.active[row].length - 1; col++) {
                if (this.active[row][col] == 1) {
                    drawSquare(this.x + col, this.y + row, VACANT)
                }
            }
        }
    }

    moveLeft() {
        if (!currentPiece.collision(-1, 0)) {
            this.undraw()
            this.x--
            this.draw()
        }
    }

    moveRight() {
        if (!currentPiece.collision(1, 0)) {
            this.undraw()
            this.x++
            this.draw()
        }
    }

    moveDown() {
        if (!currentPiece.collision(0, 1)) {
            this.undraw()
            this.y++
            this.draw()
        }
    }

    rotCCW() {
        let nextRot: number
        let prevRot: number
        let kick: number = 0

        prevRot = this.rotation
        this.undraw()

        if (this.rotation != 0) {
            nextRot = this.rotation - 1
        } else {
            nextRot = 3
        }

        this.rotation = nextRot
        this.active = this.tetromino[this.rotation]

        if (this.collision(0, 0)) {
            if (this.x < (COL) / 2) {
                kick = 1
            } else {
                kick = -1
            }
        }

        if (this.collision(kick, 0)) {
            this.rotation = prevRot
            this.active = this.tetromino[this.rotation]
            this.draw()
        } else {
            this.x = this.x + kick
            this.draw()
        }
    }

    rotCW() {
        let nextRot: number
        let prevRot: number
        let kick: number = 0

        prevRot = this.rotation
        this.undraw()

        if (this.rotation != 3) {
            nextRot = this.rotation + 1
        } else {
            nextRot = 0
        }

        this.rotation = nextRot
        this.active = this.tetromino[this.rotation]

        if (this.collision(0, 0)) {
            if (this.x < (COL)/2) {
                kick = 1
            }else{
                kick = -1
            }
        }

        if (this.collision(kick, 0)) {
            this.rotation = prevRot
            this.active = this.tetromino[this.rotation]
            this.draw()
        } else {
            this.x = this.x + kick
            this.draw()
        }
    }

    collision(x: number, y: number) {
        for (let row = 0; row <= this.active.length - 1; row++) {
            for (let col = 0; col <= this.active[row].length - 1; col++) {

                if (this.active[row][col] == 0) { continue }

                if ((this.x + col + x < 0) || (this.x + col + x > COL) || (this.y + row + y > ROW)) { return true }

                if (this.y < 0) { continue }

                if (grid[this.y + row + y][this.x + col + x] != VACANT) { return true }
            }
        }
        return false
    }

    lock() {
        for (let row = 0; row <= this.active.length - 1; row++) {
            for (let col = 0; col <= this.active[row].length - 1; col++) {
                if (this.active[row][col] == 1) {
                    grid[this.y + row][this.x + col] = this.color
                }
            }
        }
        drawGrid()
    }
}

