bgPicture.print("Niveau", 90, 20, 15)
bgPicture.print(level.toString(), 130, 20, 15)
bgPicture.print("Lignes", 90, 35, 15)
bgPicture.print(nbLines.toString(), 130, 35, 15)
//bgPicture.print("Score", 90, 90, 6)
//bgPicture.print(bestScore.toString(), 90, 97, 15)


// Create the grid with blank squares
for (let r = 0; r <= ROW; r++) {
    for (let c = 0; c <= COL; c++) {
        grid[r][c] = VACANT
    }
}
// Create difficulty
for (let r = ROW; r >= (ROW - 2*difficulty + 1); r--) {
    for (let c = 0; c <= COL; c++) {
        grid[r][c] = Math.pickRandom([VACANT, COLOR[Math.randomRange(0, 6)]])
    }
}
drawGrid()

let currentTime: number = game.runtime()

let nextPiece = new Piece()
let currentPiece: Piece
newPiece()

game.onUpdate(function () {
    if (levelChoice == false) {
        if ((game.runtime() - currentTime > speed)) {
            currentTime = game.runtime()
            if (currentPiece.collision(0, 1)) {
                if (currentPiece.y > 0) {
                    music.playTone(Note.C, 100)
                    currentPiece.lock()
                    info.changeScoreBy(Math.floor(dropPoints / 10) * (level + 1))
                    checkLine()
                    levelUp()
                    newPiece()
                    dropPoints = 0
                } else {
                    grid = undefined
                    bgPicture = undefined
                    nextPiece = undefined
                    currentPiece = undefined

                    if (bestScore < info.score()) {
                        settings.writeNumber('Score', info.score())
                    }
                    //game.over(false)
                    game.splash("PERDU", "Score : " + info.score())
                    game.reset()
                }
            } else {
                currentPiece.moveDown()
            }
        }
    }
})
